import { Component, OnInit, HostBinding, Input } from '@angular/core';
import { NgxDropzonePreviewComponent } from '../ngx-dropzone-preview.component';
import { DomSanitizer, SafeStyle } from '@angular/platform-browser';

@Component({
  selector: 'ngx-dropzone-image-preview',
  template: `
    <img [src]="imageSrc" />
		<ng-content select="ngx-dropzone-label"></ng-content>
    <ngx-dropzone-remove-badge *ngIf="removable" (click)="_remove($event)">
    </ngx-dropzone-remove-badge>
	`,
  styleUrls: ['./ngx-dropzone-image-preview.component.scss'],
  providers: [
    {
      provide: NgxDropzonePreviewComponent,
      useExisting: NgxDropzoneImagePreviewComponent
    }
  ]
})
export class NgxDropzoneImagePreviewComponent extends NgxDropzonePreviewComponent implements OnInit {

  constructor(
    sanitizer: DomSanitizer
  ) {
    super(sanitizer);
  }

  /** The file to preview. */
  @Input()
  set file(value: File) {
    this._file = value;
    this.checkFileType();
  }
  get file(): File { return this._file; }

  /** The image data source. */
  defaultImgLoading = 'data:image/svg+xml;base64,';
  imageSrc: any = this.sanitizer.bypassSecurityTrustUrl(this.defaultImgLoading);

  ngOnInit() {
    this.checkFileType();
  }

  private checkFileType() {
    const supportedTypes = [
      "image/png",
      "image/jpeg",
      "image/gif",
      "image/tiff",
      "image/bmp",
      "image/svg+xml"
    ]
    if (supportedTypes.findIndex(x => x === this.file.type) > -1 ) { this.renderImage(); }
  }
  private renderImage() {
    this.readFile()
      .then(img => setTimeout(() => {this.imageSrc = this.sanitizer.bypassSecurityTrustUrl(img as any);}))
      .catch(err => console.error(err));
  }
}
